import classes from './Header.module.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleQuestion, faCloudArrowUp, faSignIn, faSignOut, faUser } from '@fortawesome/free-solid-svg-icons';
import { Link, useLocation } from 'react-router-dom'
import { useState } from 'react';
import Button from '~/components/Layout/DefaultLayout/Header/Button';
import Menu from '~/components/Layout/DefaultLayout/Header/Popper/Menu';
import Image from '~/components/Image';
import Search from './Search';
const USER_ITEMS = [
    {
        icon: <FontAwesomeIcon icon={faUser} />,
        title: 'View profile',
        to: '/profile'
    },
    {
        icon: <FontAwesomeIcon icon={faCircleQuestion} />,
        title: 'Help',
        to: '/service',
    },

    {
        icon: <FontAwesomeIcon icon={faCloudArrowUp} />,
        title: 'Upload',
        to: '/upload',
    },
    {
        icon: <FontAwesomeIcon icon={faSignOut} />,
        title: 'Sign out',
        separate: true,
        logOut: true,
    },

]
function Header() {
    const [currentUser, setCurrentUser] = useState(() => {
        const storageUserState = JSON.parse(localStorage.getItem('user-state'));
        return storageUserState;
    });
    const path = useLocation()

    const handleLogOut = () => {
        const jsonUser = JSON.stringify(!currentUser);
        console.log(jsonUser);
        localStorage.setItem('user-state', jsonUser);
        const state = setCurrentUser(!currentUser);
        return state;
    }
    return (
        <div className={classes.wrapper}>

            <div className={classes.inner}>
                <Link to="/" className={classes.label}>
                    <div className={classes.active}>E</div>FOOD
                    {/* <img src={images.logoImage} alt="logo" className={classes['logo']} /> */}
                </Link>

                <span>
                    <Search />
                </span>

                <div className={classes.actions}>
                    <ul className={classes['menu-list']}>
                        <li className={classes['menu-item']}><Link to="/" className={`${path.pathname === ('/') ? classes.active : ''}`}>Dashboard</Link></li>
                        <li className={classes['menu-item']}><Link to="/menu" className={`${path.pathname.includes('/menu') ? classes.active : ''}`}>Menu</Link></li>
                        <li className={classes['menu-item']}><Link to="/users" className={`${path.pathname.includes('/users') ? classes.active : ''}`}>Users</Link></li>
                        {currentUser && (<li className={classes['menu-item']}><Link to="/upload" className={`${path.pathname.includes('/upload') ? classes.active : ''}`}>Upload</Link></li>)}
                    </ul>



                    {currentUser ? (
                        <Menu
                            items={USER_ITEMS}
                            handleLogOut={handleLogOut}
                        >
                            {/* <button className={classes['more-btn']}>
                                <FontAwesomeIcon icon={faBars} />
                            </button> */}
                            <div className={classes['current-user']}>
                                <Image
                                    className={classes['user-avatar']}
                                    src="data:image/jpeg;base64,"
                                    alt="avatar"
                                    fallback="data:image/jpeg;base64,"
                                />
                            </div>
                        </Menu>
                    ) : (
                        <Button
                            primary
                            rightIcon={<FontAwesomeIcon icon={faSignIn} />}
                            medium
                            // to='/login'
                            onClick={handleLogOut}
                        >
                            Sign in
                        </Button>
                    )}



                </div>
            </div>
        </div>

    );
}

export default Header;

