import classes from './Upload.module.scss'
import images from '~/assets/images'
import { faBowlFood, faBowlRice, faBurger, faIceCream, faMugHot } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useState } from "react";
const TYPE_ITEMS = [
    {
        icon: <FontAwesomeIcon icon={faBowlRice} />,
        type_food: 'Cơm'
    },

]
function Upload() {
    const [contacts, setContacts] = useState(data);
    const [addFormData, setAddFormData] = useState({
        name: "",
        address: "",
        phoneNumber: "",
        email: "",
    });
    const [editFormData, setEditFormData] = useState({
        name: "",
        address: "",
        phoneNumber: "",
        email: "",
    });
    const [editRowId, setEditRowId] = useState(null);

    const handleAddFormChange = (event) => {
        event.preventDefault();
        const fieldName = event.target.getAttribute("name");
        const fieldValue = event.target.value;

        const newFormData = { ...addFormData };
        newFormData[fieldName] = fieldValue;

        setAddFormData(newFormData);
    };

    const handleEditFormChange = (event) => {
        event.preventDefault();
        const fieldName = event.target.getAttribute("name");
        const fieldValue = event.target.value;

        const newFormData = { ...editFormData };
        newFormData[fieldName] = fieldValue;

        setEditFormData(newFormData);
    };

    const handleAddContactFormSubmit = (event) => {
        event.preventDefault();
        const newContacts = [...contacts, addFormData];
        setContacts(newContacts);
    };

    const handleEditContactFormSubmit = (event) => {
        event.preventDefault();
        const newContacts = [...contacts];

        const index = contacts.findIndex((contact) => contact.id === editRowId);
        newContacts[index] = editFormData;
        setContacts(newContacts);
        setEditRowId(null);
    };

    const handleEditClick = (event, contact) => {
        event.preventDefault();
        setEditRowId(contact.id);
        setEditFormData(contact);
    };

    const handleDeleteClick = (contactId) => {
        const newContacts = [...contacts];

        const index = contacts.findIndex((contact) => contact.id === contactId);

        newContacts.splice(index, 1);
        setContacts(newContacts);
    };

    const handleCancelClick = () => {
        setEditRowId(null);
    };
    return (
        <div className={classes.wrapper}>
            <div className={classes.title}>
                <p className={classes['title-name']}>PRODUCT MANAGEMENT</p>
                <img src={images.logoImage} alt="logo" className={classes['title-logo']} />
            </div>

            <div className={classes.filter}>
                {/* Sau này convert ra thành 1 component riêng */}
                <button className={`${classes['type-food']} ${classes.blue}`}>
                    <p className={classes['type-name']}>MÓN NƯỚC</p>
                    <FontAwesomeIcon className={classes['type-icon']} icon={faBowlFood} />
                    <p className={classes['type-quantity']}>12</p>
                </button>

                <button className={`${classes['type-food']} ${classes.red}`}>
                    <p className={classes['type-name']}>CƠM</p>
                    <FontAwesomeIcon className={classes['type-icon']} icon={faBowlRice} />
                    <p className={classes['type-quantity']}>12</p>
                </button>

                <button className={`${classes['type-food']} ${classes.purple}`}>
                    <p className={classes['type-name']}>ĐỒ UỐNG</p>
                    <FontAwesomeIcon className={classes['type-icon']} icon={faMugHot} />
                    <p className={classes['type-quantity']}>12</p>
                </button>

                <button className={`${classes['type-food']} ${classes.green}`}>
                    <p className={classes['type-name']}>TRÁNG MIỆNG</p>
                    <FontAwesomeIcon className={classes['type-icon']} icon={faIceCream} />
                    <p className={classes['type-quantity']}>12</p>
                </button>

                <button className={`${classes['type-food']} ${classes.yellow}`}>
                    <p className={classes['type-name']}>ĂN VẶT</p>
                    <FontAwesomeIcon className={classes['type-icon']} icon={faBurger} />
                    <p className={classes['type-quantity']}>12</p>
                </button>

                <form id="edit-form" onSubmit={handleEditContactFormSubmit}>
                    <table>
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Address</th>
                                <th>Phone Number</th>
                                <th>Email</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {contacts.map((contact) => (
                                // eslint-disable-next-line react/jsx-no-undef
                                <React.Fragment key={contact.id}>
                                    {editRowId === contact.id ? (
                                        <tr>
                                            <td>
                                                <input
                                                    type="text"
                                                    required="required"
                                                    placeholder="Name"
                                                    name="name"
                                                    value={editFormData.name}
                                                    onChange={handleEditFormChange}
                                                />
                                            </td>
                                            <td>
                                                <input
                                                    type="text"
                                                    required="required"
                                                    placeholder="Address"
                                                    name="address"
                                                    value={editFormData.address}
                                                    onChange={handleEditFormChange}
                                                />
                                            </td>
                                            <td>
                                                <input
                                                    type="text"
                                                    required="required"
                                                    placeholder="Phone Number"
                                                    name="phoneNumber"
                                                    value={editFormData.phoneNumber}
                                                    onChange={handleEditFormChange}
                                                />
                                            </td>
                                            <td>
                                                <input
                                                    type="text"
                                                    required="required"
                                                    placeholder="Email"
                                                    name="email"
                                                    value={editFormData.email}
                                                    onChange={handleEditFormChange}
                                                />
                                            </td>
                                            <td>
                                                <button type="submit">Save</button>
                                                <button onClick={handleCancelClick}>Cancel</button>
                                            </td>
                                        </tr>
                                    ) : (
                                        <tr>
                                            <td>{contact.name}</td>
                                            <td>{contact.address}</td>
                                            <td>{contact.phoneNumber}</td>
                                            <td>{contact.email}</td>
                                            <td>
                                                <button
                                                    type="button"
                                                    onClick={(event) => handleEditClick(event, contact)}
                                                >
                                                    Edit
                                                </button>
                                                <button
                                                    type="button"
                                                    onClick={() => handleDeleteClick(contact.id)}
                                                >
                                                    Delete
                                                </button>
                                            </td>
                                        </tr>
                                    )}
                                </React.Fragment>
                            ))}
                        </tbody>
                    </table>
                </form>

                <form onSubmit={handleAddContactFormSubmit}>
                    <input
                        type="text"
                        required="required"
                        placeholder="Name"
                        name="name"
                        onChange={handleAddFormChange}
                    />
                    <input
                        type="text"
                        required="required"
                        placeholder="Address"
                        name="address"
                        onChange={handleAddFormChange}
                    />
                    <input
                        type="text"
                        required="required"
                        placeholder="Phone Number"
                        name="phoneNumber"
                        onChange={handleAddFormChange}
                    />
                    <input
                        type="email"
                        required="required"
                        placeholder="Email"
                        name="email"
                        onChange={handleAddFormChange}
                    />
                    <button>Add</button>
                </form>

            </div>
        </div>
    );
}

export default Upload;
